const testWrapper = document.querySelector(".test-wrapper");
const testArea = document.querySelector("#test-area");
const fileInput = document.querySelector("#fileInput");
const resetButton = document.querySelector("#reset");
const theTimer = document.querySelector(".timer");

var timer = [0,0,0,0];
var interval;
var timerRunning = false;

// Predefined paragraphs
var paragraphs = [
    'The quick brown fox jumps over the lazy dog.',
    'Pack my box with five dozen liquor jugs.',
    'How vexingly quick daft zebras jump!',
    // Add more paragraphs as needed
];

// Function to set a new paragraph
function setParagraph(paragraph) {
    originText = paragraph;
    document.querySelector("#origin-text p").innerHTML = originText;
}

// Event listener for the paste option
document.querySelector("#paste").addEventListener('click', function() {
    var text = prompt('Please paste your text here:');
    setParagraph(text);
});

// Event listener for the predefined paragraphs option
document.querySelector("#predefined").addEventListener('click', function() {
    var index = prompt('Please enter the number of the paragraph (0-' + (paragraphs.length-1) + '):');
    setParagraph(paragraphs[index]);
});

// Add leading zero to numbers 9 or below (purely for aesthetics):
function leadingZero(time) {
	if (time <= 9) {
		time = "0" + time;
	}
	return time;
}

// Run a standard minute/second/hundredths timer:
function runTimer() {
	let currentTime = leadingZero(timer[0]) + ":" + leadingZero(timer[1]) + ":" + leadingZero(timer[2]);
	theTimer.innerHTML = currentTime;
	timer[3]++;

	timer[0] = Math.floor((timer[3]/100)/60);
	timer[1] = Math.floor((timer[3]/100) - (timer[0] * 60));
	timer[2] = Math.floor(timer[3] - (timer[1] * 100) - (timer[0] * 6000));
}

// Match the text entered with the provided text on the page:
function spellCheck() {
	let textEntered = testArea.value;
	let originTextMatch = originText.substring(0,textEntered.length);

	if (textEntered == originText) {
		clearInterval(interval);
		testWrapper.style.borderColor = "#429890";
	} else {
		if (textEntered == originTextMatch) {
			testWrapper.style.borderColor = "#65CCf3";
		} else {
			testWrapper.style.borderColor = "#E95D0F";
		}
	}

}

// Start the timer:
function start() {
	let textEnterdLength = testArea.value.length;
	if (textEnterdLength === 0 && !timerRunning) {
		timerRunning = true;
		interval = setInterval(runTimer, 10);
	}

}

// Reset everything:
function reset() {
	clearInterval(interval);
	interval = null;
	timer = [0,0,0,0];
	timerRunning = false;

	testArea.value = "";
	theTimer.innerHTML = "00:00:00";
	testWrapper.style.borderColor = "grey";
}

// Event listeners for keyboard input and the reset
testArea.addEventListener("keypress", start, false);
testArea.addEventListener("keyup", spellCheck);
resetButton.addEventListener("click", reset, false);

fileInput.addEventListener('change', function(e) {
	//get the selected file
	var file = e.target.files[0];

	//create a new FileReader object
	var reader = new FileReader();

	//add an event listener for the load event
	reader.addEventListener('load', function() {
		//the file's text will be printed here
		setParagraph(reader.result);
	});
	//read the file as text
	reader.readAsText(file);
});
